import React from "react";
import styled from "styled-components";
import {useState} from "react";
import {useEffect} from "react";
import axios from "axios";


const Select = styled.select`
    padding: 5px;
    width: 500px;
`;

const Button = styled.button`
    padding: 10px;
    background-color: black;
    color: white;
    border: none;
`;

const viewAirports = () => {

}
const Search = () => {
    const [airports, setAirports] = useState([]);
    const [fromAirport, setFromAirport] = useState("");
    const [toAirport, setToAirport] = useState("");

    useEffect(() => {
        const getAirports = async () => {
            try {
                const response = await axios.get("/airport");
                console.log(response.data)
                setAirports(response.data);
            } catch (error) {
                console.log(error)
            }
        }
        getAirports()
    }, []);




    return (
        <form>
            <Select value={fromAirport} onChange={(e) => setFromAirport(e.target.value)}>
                <option value="" disabled selected>From...</option>
                {airports.map((airport) => (
                    <option key={airport.code} value={airport.code}>{airport.name}</option>
                ))}
            </Select>

            <Select>
                <option value="" disabled selected>To...</option>
                {/*{airports.map((airport) = (*/}
                {/*    <option> key={airport.code} value={airport.code}</option>,*/}
                {/*{airport.name}*/}
                {/*    ))}*/}
            </Select>
            <Button >Search Flights</Button>
        </form>
    )

}

export default Search;
