import React, {useContext} from "react";
import Navbar from "../components/StaffNavbar";
import styled from "styled-components";
import {AuthContext} from "../context/authContext";
import Search from "../components/FlightSearch";
import AddFlight from "../components/AddFlight";

const Container = styled.div`
    height: 60px
`;

const Wrapper = styled.div`
    padding: 10px;
    display: flex;
    align-items: center;
    justify-content: space-between;
`;

const Center = styled.div`
    padding-top: 50px;
    flex: 1;
    text-align: left;
    justify-content: space-between;
`;

const Left = styled.div`
    padding-bottom: 50px;
    flex: 1;
    text-align: center;
    justify-content: space-between;
`;

const Right = styled.div`
    flex: 1;
    text-align: center;
    justify-content: space-between;
`;

const Greeting = styled.h2`
    padding-bottom: 150px;
`;

const Info = styled.h3`
    padding-top: 50px;
    padding-bottom: 20px;
`;



const StaffProfile = () => {
    const {currentStaff} = useContext(AuthContext);
    console.log(currentStaff.airline_name);

    return (
        <div>
            <Navbar/>
            <Container>
                <Wrapper>
                    <Left>
                        <Greeting>Hello, {currentStaff ? currentStaff.first_name : 'Guest'}</Greeting>
                    </Left>
                    <Center>
                        <Info>Next {currentStaff.airline_name} Flights</Info>

                        <Info>Add Flight</Info>
                        <AddFlight/>
                    </Center>
                    <Right></Right>
                </Wrapper>
            </Container>
        </div>
    )
}

export default StaffProfile;
