import React from "react";
import styled from "styled-components";
import {useState} from "react";
import {useEffect} from "react";
import axios from "axios";


const Select = styled.select`
    padding: 5px;
    width: 500px;
`;

const Wrapper = styled.div`
    padding-left: 200px;
    align-items: center;
`;

const Button = styled.button`
    padding: 10px;
    background-color: black;
    color: white;
    border: none;
`;


const Bar = styled.div`
    padding: 10px 0px;
`;

const Search = () => {
    const [airports, setAirports] = useState([]);
    const [fromAirport, setFromAirport] = useState("");
    const [toAirport, setToAirport] = useState("");

    useEffect(() => {
        const getAirports = async () => {
            try {
                const response = await axios.get("/airport");
                console.log(response.data)
                setAirports(response.data);
            } catch (error) {
                console.log(error)
            }
        }
        getAirports()
    }, []);




    return (
        <form>
        <Bar>
            <Select value={fromAirport} onChange={(e) => setFromAirport(e.target.value)}>
                <option value="" disabled selected>From...</option>
                {airports.map((airport) => (
                    <option key={airport.code} value={airport.code}>{airport.name}</option>
                ))}
            </Select>
        </Bar>
        <Bar>
            <Select>
                <option value={toAirport} disabled selected>To...</option>
                {airports.map((airport) => (
                    <option key={airport.code} value={airport.code}>{airport.name}</option>
                ))}
            </Select>
        </Bar>
            <Wrapper>
                <Button >Search Flights</Button>
            </Wrapper>
        </form>
    )

}

export default Search;
